import 'package:ecommerce_firebase/utils/shared_pref.dart';import 'package:ecommerce_firebase/utils/storage_key.dart';import '../model/user_profile_model.dart';import '../utils/all_instance.dart';Future<void> getUserProfileDataRequestApi(    {path, required List<UserProfileModel> list}) async {  try {    final collection = await path.doc(Instance.userEmail).get();    // Check if the document exists    if (collection.exists) {      final data = collection.data() as Map<String, dynamic>;      final userProfile = UserProfileModel(        address: data['Address'],        name: data['Name'],        fcmToken: data['FcmToken'],        password: data['Password'],        mobile: data['Mobile'],        photo: data['Photo'],        email: data['Email'],      );      // Add the user profile to the list      list.add(userProfile);      storageInstance.write(StorageKey.setNameKey, data['Name']);      storageInstance.write(StorageKey.setFcmToken, data['FcmToken']);      storageInstance.write(StorageKey.setPasswordKey, data['Password']);      storageInstance.write(StorageKey.setMobileKey, data['Mobile']);      storageInstance.write(StorageKey.setPhotoKey, data['Photo']);      storageInstance.write(StorageKey.setEmailKey, data['Email']);    } else {      print('Document does not exist');    }  } catch (e) {    print('Error fetching user profile: $e');  }}