import '../model/user_profile_model.dart';import '../utils/all_instance.dart';Future<void> getUserProfileDataRequestApi(    {path, required List<UserProfileModel> list}) async {  try {    final collection = await path.doc(Instance.userEmail).get();    // Check if the document exists    if (collection.exists) {      final data = collection.data() as Map<String, dynamic>;      final userProfile = UserProfileModel(        address: data['Address'],        name: data['Name'],        fcmToken: data['FcmToken'],        password: data['Password'],        mobile: data['Mobile'],        photo: data['Photo'],        email: data['Email'],      );      // Add the user profile to the list      list.add(userProfile);      print(userProfile); // Print the user profile data    } else {      print('Document does not exist');    }  } catch (e) {    print('Error fetching user profile: $e');  }}