import 'package:flutter_local_notifications/flutter_local_notifications.dart';import 'package:get/get.dart';FlutterLocalNotificationsPlugin flutterLocalNotificationsPlugin =    FlutterLocalNotificationsPlugin();class NotificationService {  initializeApp() async {    var initializationSettingsAndroid =        const AndroidInitializationSettings('@mipmap/ic_launcher');    var initializationSettings = InitializationSettings(      android: initializationSettingsAndroid,    );    flutterLocalNotificationsPlugin.initialize(      initializationSettings,    );  }  Future showNotification(      int id, String title, String body, String payload) async {    var androidPlatformChannelSpecifics = const AndroidNotificationDetails(        'your_channel_id', 'your_channel_name',        importance: Importance.max, priority: Priority.high, ticker: 'ticker');    var platformChannelSpecifics = NotificationDetails(      android: androidPlatformChannelSpecifics,    );    await flutterLocalNotificationsPlugin        .show(id, title, body, platformChannelSpecifics, payload: payload);  }}