import '../../../../utils/export.dart';class BannerCard extends StatelessWidget {  final String img;  final String name;  final String price;  final VoidCallback buyNow;  final VoidCallback isFav;  const BannerCard({    Key? key,    required this.img,    required this.name,    required this.price,    required this.buyNow,    required this.isFav,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Card(      elevation: 4.0,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(10.0),      ),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Row(            children: [              ClipRRect(                  borderRadius: const BorderRadius.only(                      topLeft: Radius.circular(10.0),                      topRight: Radius.circular(10.0)),                  child: Image.network(img,                      height: 100.0,                      fit: BoxFit.cover,                      errorBuilder: (context, error, stackTrace) => Image.asset(                          'assets/images/no_internet.jpg',                          height: 100))),              const Spacer(),              Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  Padding(                    padding: const EdgeInsets.all(8.0),                    child: CommonText(                      text: name,                      fontWeight: FontWeight.bold,                    ),                  ),                  Padding(                    padding: const EdgeInsets.symmetric(horizontal: 8.0),                    child: CommonText(                      text: 'Price: \$$price',                      fontWeight: FontWeight.bold,                    ),                  ),                  const SizedBox(height: kTooSmallSize),                  Row(                    children: [                      Padding(                        padding: const EdgeInsets.symmetric(horizontal: 8.0),                        child: ElevatedButton(                            style: ElevatedButton.styleFrom(                                shape: const RoundedRectangleBorder(                                    borderRadius: BorderRadius.only(                                        topRight:                                            Radius.circular(kPrimaryBigSize))),                                backgroundColor: AppColor.kPurpleColor),                            onPressed: buyNow,                            child: const Text(AppString.buyNow)),                      ),                      IconButton(                          onPressed: isFav,                          icon: const Icon(Icons.heart_broken,                              color: AppColor.kRedColor)),                    ],                  ),                ],              )            ],          ),        ],      ),    );  }}