import 'package:ecommerce_firebase/modules/screens/home%20screen/component/product_card.dart';import 'package:shimmer/shimmer.dart';import '../../../../utils/export.dart';class CustomProductGrid extends StatelessWidget {  final List<ProductModel> productList;  final int maxProducts;  const CustomProductGrid({    super.key,    required this.productList,    this.maxProducts = 6,  });  @override  Widget build(BuildContext context) {    return GetBuilder<HomeScreenController>(      builder: (controller) {        int length = controller.productList.length;        return GridView.builder(          physics: const NeverScrollableScrollPhysics(),          shrinkWrap: true,          itemCount: length > maxProducts ? maxProducts : length,          gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(              crossAxisCount: 3,              childAspectRatio: 0.7,              crossAxisSpacing: 10,              mainAxisSpacing: 10),          itemBuilder: (context, index) {            ProductModel data = controller.productList[index];            return ProductCard(                img: data.img.toString(),                name: data.name.toString(),                price: data.price.toString(),                buyNow: () {},                isFav: () {});          },        );      },    );  }}