import '../../../../utils/export.dart';class CustomSliderWidget extends StatelessWidget {  final List<SliderImageModel> sliderImageList;  const CustomSliderWidget({    super.key,    required this.sliderImageList,  });  @override  Widget build(BuildContext context) {    return GetBuilder<HomeScreenController>(      builder: (controller) {        return SliderWidget(          items: controller.sliderImageList              .map((slider) => Image.network(                    slider.img.toString(),                    fit: BoxFit.fill,                    errorBuilder: (context, error, stackTrace) =>                        Image.asset('assets/images/no_internet.jpg'),                  ))              .toList(),        );      },    );  }}