import 'package:shimmer/shimmer.dart';import '../../../../utils/export.dart';class CustomSliderWidget extends StatelessWidget {  final List<SliderImageModel> sliderImageList;  const CustomSliderWidget({    super.key,    required this.sliderImageList,  });  @override  Widget build(BuildContext context) {    return GetBuilder<HomeScreenController>(      builder: (controller) => SliderWidget(        items: sliderImageList            .map((slider) => controller.isLoading                ? Image.network(slider.img.toString(),                    fit: BoxFit.fill,                    errorBuilder: (context, error, stackTrace) =>                        Image.asset('assets/images/no_internet.jpg'))                : Shimmer.fromColors(                    baseColor: Colors.grey.shade300,                    highlightColor: Colors.grey.shade100,                    child: Image.network(slider.img.toString(),                        fit: BoxFit.fill,                        errorBuilder: (context, error, stackTrace) =>                            Image.asset('assets/images/no_internet.jpg'))))            .toList(),      ),    );  }}