import 'package:ecommerce_firebase/utils/all_instance.dart';import '../../../../utils/export.dart';import '../../../../utils/store_data.dart';import '../../../../utils/user_collection.dart';import '../../../widget/photos_card.dart';class UserDrawer extends StatelessWidget {  const UserDrawer({    super.key,  });  @override  Widget build(BuildContext context) {    return Drawer(      child: FutureBuilder<DocumentSnapshot>(        future: Collection.collectionProfile.doc('${Instance.userEmail}').get(),        builder:            (BuildContext context, AsyncSnapshot<DocumentSnapshot> snapshot) {          if (snapshot.hasError) {            return const Text("Something went wrong");          }          if (snapshot.hasData && !snapshot.data!.exists) {            return const Text("Document does not exist");          }          if (snapshot.connectionState == ConnectionState.done) {            Map<String, dynamic> data =                snapshot.data!.data() as Map<String, dynamic>;            ListView(              children: [                UserAccountsDrawerHeader(                  currentAccountPictureSize: const Size(60, 60),                  accountName: GetBuilder<HomeScreenController>(                    builder: (controller) => CommonText(                      onTap: () {                        Get.toNamed(RouteName.updateProfileScreen);                      },                      text: '${data['Name']}',                      color: AppColor.kWhiteColor,                    ),                  ),                  accountEmail: GetBuilder<HomeScreenController>(                    builder: (controller) => SubtitleText(                      text: '${data['Email']}',                      color: AppColor.kWhiteColor.withOpacity(0.7),                    ),                  ),                  currentAccountPicture: CircleAvatar(                    radius: 50,                    child: PhotosCard(                      img: decodeBase64Image(UserData.userPhoto),                    ),                  ),                  decoration:                      const BoxDecoration(color: AppColor.kPrimaryColor),                ),                ListTile(                  leading: const Icon(Icons.help),                  title: const Text('Help Center'),                  onTap: () {},                ),                ListTile(                  leading: const Icon(Icons.favorite),                  title: const Text('Favorites'),                  onTap: () {},                ),                const Divider(),                ListTile(                  leading: const Icon(Icons.history),                  title: const Text('History'),                  onTap: () {},                ),                ListTile(                  leading: const Icon(Icons.settings),                  title: const Text('Settings'),                  onTap: () {},                ),                ListTile(                  leading: const Icon(Icons.info),                  title: const Text('About'),                  onTap: () {},                ),                ListTile(                  leading: const Icon(Icons.logout),                  title: const Text('Log Out'),                  onTap: () {},                ),              ],            );          }          return const Text("loading");        },      ),    );  }}