import 'package:ecommerce_firebase/modules/screens/splash%20screen/controller/splash_screen_controller.dart';import 'package:flutter_animate/flutter_animate.dart';import '../../../../utils/export.dart';class SplashScreen extends GetView<SplashScreenController> {  const SplashScreen({super.key});  @override  Widget build(BuildContext context) {    controller.authStateChanges();    return Scaffold(      body: Padding(        padding: const EdgeInsets.all(33.0),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: <Widget>[            const Spacer(),            Center(              child: Column(                mainAxisSize: MainAxisSize.min,                children: [                  Image.asset('assets/images/app_logo.png',                  height: Get.width < 500 ? Get.width * 0.25 : Get.width * 0.15),                  Text('E-Bazar',                    style: headlineLarge(color: Colors.orange),                  )                ].animate(interval: 1000.ms).fadeIn(duration: 250.ms).slideY(duration: 450.ms),              )            ),            const Spacer(),            const CircularProgressIndicator(color: Colors.orange),            const SizedBox(height: 12.0,),            Text('Version: ${AppInfo.appVersion}',              style: titleTooSmall(color: Colors.grey),            )          ],        ),      ),    );  }}